            'jam' => $this->input->post('jam'),
            'harga' => $this->input->post('harga'),
            'sisa_tiket' => $this->input->post('sisa_tiket'),
        );
        
        $this->session->set_userdata(
        $data = array(
                        'kd_tiket' => $this->input->post('kodetiket'),
