class Pelanggan extends CI_Controller{
    function __construct()
    {
        parent::__construct();
        $this->load->model('Pelanggan_model');
    } 

    /*
     * Listing of pelanggan
     */
    function index()
    {
        $params['limit'] = RECORDS_PER_PAGE; 
        $params['offset'] = ($this->input->get('per_page')) ? $this->input->get('per_page') : 0;
        
        $config = $this->config->item('pagination');
        $config['base_url'] = site_url('pelanggan/index?');
        $config['total_rows'] = $this->Pelanggan_model->get_all_pelanggan_count();
        $this->pagination->initialize($config);

        $data['pelanggan'] = $this->Pelanggan_model->get_all_pelanggan($params);
        
        $data['_view'] = 'pelanggan/index';
        $this->load->view('layouts/main',$data);
    }

    /*
     * Adding a new pelanggan
     */
    function add()
    {   
        $this->load->library('form_validation');

		$this->form_validation->set_rules('password','Password','required|max_length[20]');
		$this->form_validation->set_rules('nama','Nama','max_length[50]');
		$this->form_validation->set_rules('alamat','Alamat','max_length[255]');
		$this->form_validation->set_rules('username','Username','required|max_length[15]');
		
		if($this->form_validation->run())     
        {   
            $params = array(
				'password' => $this->input->post('password'),
				'nama' => $this->input->post('nama'),
				'alamat' => $this->input->post('alamat'),
				'username' => $this->input->post('username'),
            );
            
            $pelanggan_id = $this->Pelanggan_model->add_pelanggan($params);
            redirect('pelanggan/index');
        }
        else
        {            
            $data['_view'] = 'pelanggan/add';
            $this->load->view('layouts/main',$data);
        }
    }  

    /*
     * Editing a pelanggan
     */
    function edit($id_pelanggan)
    {   
        // check if the pelanggan exists before trying to edit it
        $data['pelanggan'] = $this->Pelanggan_model->get_pelanggan($id_pelanggan);
        
        if(isset($data['pelanggan']['id_pelanggan']))
        {
            $this->load->library('form_validation');

			$this->form_validation->set_rules('password','Password','required|max_length[20]');
			$this->form_validation->set_rules('nama','Nama','max_length[50]');
			$this->form_validation->set_rules('alamat','Alamat','max_length[255]');
			$this->form_validation->set_rules('username','Username','required|max_length[15]');
		
			if($this->form_validation->run())     
            {   
                $params = array(
					'password' => $this->input->post('password'),
					'nama' => $this->input->post('nama'),
					'alamat' => $this->input->post('alamat'),
					'username' => $this->input->post('username'),
                );

                $this->Pelanggan_model->update_pelanggan($id_pelanggan,$params);            
                redirect('pelanggan/index');
            }
            else
            {
                $data['_view'] = 'pelanggan/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
            show_error('The pelanggan you are trying to edit does not exist.');
    } 

    /*
     * Deleting pelanggan
     */
    function remove($id_pelanggan)
    {
        $pelanggan = $this->Pelanggan_model->get_pelanggan($id_pelanggan);

        // check if the pelanggan exists before trying to delete it
        if(isset($pelanggan['id_pelanggan']))
        {
            $this->Pelanggan_model->delete_pelanggan($id_pelanggan);
            redirect('pelanggan/index');
        }
        else
            show_error('The pelanggan you are trying to delete does not exist.');
    }
    
}
